
<?php
// Assuming the rest of the script is already included here
// ...

// Check if the upload directory exists and is writable
$upload_dir = 'uploads/media/';
if (!is_dir($upload_dir) && !mkdir($upload_dir, 0777, true)) {
    die('Failed to create upload directory.');
}

// Check if a file was uploaded without errors
if (isset($_FILES['cover_image_file']['error']) && $_FILES['cover_image_file']['error'] === UPLOAD_ERR_OK) {
    // Process the uploaded file
    $file_tmp_path = $_FILES['cover_image_file']['tmp_name'];
    $file_name = basename($_FILES['cover_image_file']['name']);
    $target_file_path = $upload_dir . $file_name;
    if (move_uploaded_file($file_tmp_path, $target_file_path)) {
        $cover_image = $target_file_path;
    } else {
        die('Failed to move uploaded file.');
    }
} elseif (!empty($_POST['cover_image_url'])) {
    // Use the cover image provided by URL
    $cover_image = $_POST['cover_image_url'];
} else {
    // No cover image provided
    $cover_image = null;
}

// Prepare and execute the SQL query to insert the data
$query = "INSERT INTO movies (title, description, release_year, external_link, cover_image) VALUES (?, ?, ?, ?, ?)";
$stmt = $pdo->prepare($query);
if (!$stmt->execute([$_POST['title'], $_POST['description'], $_POST['release_year'], $_POST['external_link'], $cover_image])) {
    die('Failed to insert data into the database.');
}

// Redirect or output success message
echo 'Content added successfully!';
// ...
?>


